package Chap02;

public class InputOutput02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String myName = "홍길동";  //문자열 자료형
		int myAge = 20; //정수 자료형
		
		//이스케이프 문자 포함하여 값 출력하기
		System.out.print("이름 : \t" + myName + "\n"); //이름 값, 탭과 줄바꿈 문자 사용하여 출력
		System.out.print("나이 : \t" + myAge + "\n"); //나이 값, 탭과 줄바꿈 문자 사용하여 출력

	}

}
