package Chap03;

public class Example06 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 100;
		long l = i; // int형태를 그대로 long에 넣음
		float f = l;// float형태에 그대로 넣음

		System.out.println(i); // i를 출력
		System.out.println(l); // l를 출력
		System.out.println(f); // f를 출력

	}

}
