package Chap03;

public class Example07 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double d = 3.14;
		long l = (long) d; // 형변환을 강제로해서 넣어줌
		int i = (int) l; // 형변환을 강제로해서 넣어줌

		System.out.println(d); // 실수 double형 d가 출력
		System.out.println(l); // 정수 long형 l가 출력
		System.out.println(i); // 정수 int형 i가 출력

	}

}
