package Chap03;

public class Operator05 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10, b = 10, c = 10, d = 10;

		System.out.println("a++ =>" + (a++)); // a의 값을 출력하고 더함(10)
		System.out.println("a =>" + a); // a출력 (11)
		System.out.println("++b =>" + (++b)); // b에 값을 더하고 출력(11)
		System.out.println("c-- =>" + (c--)); // c의 값을 출력하고 뺌(10)
		System.out.println("--d =>" + (--d)); // d에 값을 빼고 출력(9)

	}

}
