package Chap04;

public class Example04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 70; //변수 선언,초기화
		if(num < 100) //num이 100보다 작으면
		{ 
		System.out.println("숫자가 100보다 작습니다."); //문자열 출력
	      if(num > 50) //num이 50보다 크면
	      { 
	       System.out.println("숫자가 50보다 큽니다."); //if내 if문이라 조건이 만족하면 뒤에 바로 문자열 출력	    	
	      }
		}
	}

}
